function blacklistFilter($arg) {
  $blacklist = array('[', ']', ';', '?', '@', '(', ')', 'exec', 'eval', '$', 'phpinfo', 'flag', 'data', 'filter', '#');
  $filteredInput = str_replace($blacklist, '', $arg);
  return $filteredInput;
}
