  public function __toString(){
    ($this -> obj)();
    return "1";
  }
  public function evil($c){
    eval($c);
  }
  public function __sleep(){
    phpinfo();
  }
}

class MM{
  public $name;
  public $c;
  public function __invoke(){
    ($this->name)($this->c);
  }
  public function __toString(){
    return "ok,but wrong";
  }
  public function __call($a, $b){
    echo "Hacker!";
  }
}
$a = unserialize($_GET['bbb']);
throw new Error("NoNoNo");
